<style>
    body {
        height: 100%;
        position: relative;
    }
    .logo {
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%,-50%);

        display: flex;
        flex-direction: column;

        justify-content: center;
        height: 100%;
    }
    .logo__svg {
        position: relative;
        height: 200px;
        display: flex;
        justify-content: center;
    }
    .logo__svg-fork {
        position: absolute;
        transform-origin: 50% 50%;
        height: 100%;
        width: 43px;
        animation: fork-step 5s .3s ease-out infinite;
        transform: rotate(0) translateX(-116px);
    }
    .logo__svg-plate {
        height: 100%;
    }
    .logo__svg-soon {
        position: absolute;
        transform-origin: 50% 50%;
        height: 100%;
        width: 43px;
        animation: soon-step 5s .3s ease-out infinite;
        transform: rotate(0) translateX(116px);
    }
    @keyframes fork-step {
        0% {
            transform: rotate(0) translateX(-116px);
        }

        15.5% {
            transform: rotate(42deg) translateX(0);
        }
        31% {
            transform: rotate(223deg) translateX(0);
        }

        46.5% {
            transform: rotate(180deg) translateX(-116px);
        }
        50% {
            transform: rotate(180deg) translateX(-116px);
        }

        65.5% {
            transform: rotate(223deg) translateX(0);
        }
        81% {
            transform: rotate(42deg) translateX(0);
        }

        96.5% {
            transform: rotate(0) translateX(-116px);
        }
        100% {
            transform: rotate(0) translateX(-116px);
        }
    }

    @keyframes soon-step {
        0% {
            transform: rotate(0) translateX(116px);
        }

        15.5% {
            transform: rotate(-47deg) translateX(0);
        }
        31% {
            transform: rotate(132deg) translateX(0);
        }

        46.5% {
            transform: rotate(180deg) translateX(116px);
        }
        50% {
            transform: rotate(180deg) translateX(116px);
        }

        65.5% {
            transform: rotate(132deg) translateX(0);
        }
        81% {
            transform: rotate(-47deg) translateX(0);
        }

        96.5% {
            transform: rotate(0) translateX(116px);
        }
        100% {
            transform: rotate(0) translateX(116px);
        }
    }
    .logo__box {
        display: flex;
        align-items: flex-end;
        margin-top: 40px;

        justify-content: center;
    }
    .logo__dots {
        display: flex;
        position: relative;
        width: 56px;
        height: 10px;
        transform: scale(-1, 1);
        margin: 0 0 8px 6px;
    }
    .logo__dots span {
        position: absolute;
        width: 10px;
        height: 10px;
        background: #b27dff;
        border-radius: 50%;
        animation: l-dots 1s infinite ease-in-out;
    }
    .logo__dots span:nth-child(1) {
        left: 0px;
        animation-delay: 0.6s;
    }
    .logo__dots span:nth-child(2) {
        left: 15px;
        animation-delay: 0.4s;
    }
    .logo__dots span:nth-child(3) {
        left: 30px;
        animation-delay: 0.2s;
    }
    .logo__dots span:nth-child(4) {
        left: 45px;
        animation-delay: 0.1s;
    }
    @keyframes l-dots {
        0%,
        80%,
        100% {
            transform: scale(0);
            opacity: 0;
        }
        40% {
            transform: scale(1);
            opacity: 1;
        }
    }
    @-webkit-keyframes l-dots {
        0%,
        80%,
        100% {
            transform: scale(0);
            opacity: 0;
        }
        40% {
            transform: scale(1);
            opacity: 1;
        }
    }
    .logo__text {
        font-size: 40px;
        color: #b27dff;
        font-family: "Quattrocento Sans", sans-serif;
    }

</style>
